import React, {Component} from 'react'

class EditTodo extends Component {
    state = this.props.location.state.todo
    
    
    login = (e) => {
        e.preventDefault();
        // this.props.addTodo(this.state);
        // this.setState({
        //     content:""
        // })

//        this.props.history.push('/todos')
    }
    render(){
        return(
            <div className="container">
            <h4 className="center">Todo Item</h4>
            <div className="row">
                <form className="col s12" onSubmit={this.login}>
                <div class="row">
                    <div class="input-field col s6">
                    <input placeholder="Placeholder" id="first_name" type="text" class="validate"/>
                    <label for="first_name">First Name</label>
                    </div>
                 </div>
                <div className="row">
                    <div className="input-field col s12">
                    <input id="title" type="text" className="validate" value={this.state.title}/>
                    <label for="title">Title</label>
                    </div>
                </div>
                <div className="row">
                    <div className="input-field col s12">
                        <input id="email" type="email" className="validate" />
                        <label for="email">Email</label>
                    </div>
                </div>
                <button className="btn waves-effect waves-light" type="submit" name="action">Update
                    <i className="material-icons right"></i>
                </button>
                </form>
            </div>
        </div>
        )
    }
}

export default EditTodo
