import React from 'react'
import { Link } from 'react-router-dom'
const Todos = ({todos, markTodo, deleteTodo}) => {
    
    const todoList = todos.length ? (
        todos.map(todo => {
            return (
                <div className="collection-item" key={todo.id}>
                <p>
                 <label>
                    <input type="checkbox" className="filled-in" checked={todo.status? "checked" : ""} onChange={() => markTodo(todo.id)}/>
                    <span></span>
                </label>
                </p>
                <Link>{todo.content}</Link>
                    
                    <a className="small material-icons red-text text-lighten-2 right" onClick={() => deleteTodo(todo.id)}>close</a>
                </div>
            )
        })
    ) : (
        <p className="center">You have no Todos</p>
    );

    return (
        <div className="todos collection">
            {todoList}
        </div>
    )
    
}

export default Todos;
