import React from 'react'
import Moment from 'react-moment'

// import { Link } from 'react-router-dom'
const Todos = ({todos, markTodo, deleteTodo}) => {
    const calendarStrings = {
        lastDay : '[Yesterday]',
        sameDay : '[Today,] LT',
        nextDay : '[Tomorrow,] LT',
        lastWeek : '[last] dddd',
        nextWeek : 'dddd',
        sameElse : 'L'
    };
    const todoList = todos.length ? (
        todos.map(todo => {
            return (
                
                <li className="collection-item avatar" key={todo.id}>
                    <label className="circle">
                        <input type="checkbox" className="" checked={todo.status? "checked" : ""} onChange={() => markTodo(todo.id)}/>
                        <span></span>
                    </label>
                    <p className="title">{todo.title}</p>
                    <p className="grey-text text-darken-1">{todo.description}</p>
                    <a className="datetime waves-effect waves-light white teal-text btn {todo.due_at > new Date; }"><i className="material-icons left">today</i><Moment calendar={calendarStrings}>{todo.due_at}</Moment></a>
                    <a className="delete-btn secondary-content small material-icons red-text text-lighten-2 right" onClick={() => deleteTodo(todo.id)}>delete_forever</a>                    
                </li>
            )
        })
    ) : (
        <p className="center">You have no Todos</p>
    );

    return (
        <ul className="todos collection">
            {todoList}
        </ul>
    )
    
}


export default Todos;
