import React from 'react'

const Todos = ({todos, markTodo, deleteTodo}) => {
    
    const todoList = todos.length ? (
        todos.map(todo => {
            return (
                <div className="collection-item" key={todo.id}>
                 <label>
                    <input type="checkbox" className="filled-in" checked={todo.status? "checked" : ""} onChange={() => markTodo(todo.id)}/>
                    <span>{todo.content}</span>
                    
                    {/* <button className="btn-floating waves-effect waves-light btn-small red lighten-2 right no-padding"  ></button> */}
                    <i className="small material-icons text-red" onClick={() => deleteTodo(todo.id)}>close</i>
                    {/* <a class=" btn-small waves-effect waves-light red lighten-1 right "><i class="material-icons">close</i></a> */}
                </label>
                </div>
            )
        })
    ) : (
        <p className="center">You have no Todos</p>
    );

    return (
        <div className="todos collection">
            {todoList}
        </div>
    )
    
}

export default Todos;
