import React, { Component } from 'react'
import Todo from './Todo'
import AddTodo from './AddTodo'
import axios from 'axios'


class Todos extends Component {
    state = {
        todos: [
            // {id: 1, title: "Buy Milk", description: "I am a very simple card. I am good at containing small bits of information.",  due_at:"2020-07-26 19:30:00", status: "Active", editing: false},
            // {id: 2, title: "Buy Something", due_at:"2020-07-24 19:30:00",  status: "Active", editing: false},
            // {id: 3, title: "Date Next week", due_at:"2020-07-29 19:30:00",  status: "Completed", editing: false},
            // {id: 4, title: "Date Next week", due_at:"2020-08-05 19:30:00",  status: "Active", editing: false},
            // {id: 5, title: "Last Week Date ", due_at:"2020-06-19 19:30:00",  status: "Active", editing: false}
          ]
    };
    componentDidMount(){
        axios.defaults.baseURL = 'http://localhost:8000';
        axios.get('/todos/')
        .then(res => {
            this.setState({
                todos: res.data.filter(todo => {
                  return (todo.status != 'Deleted')
                })
            })
        })
        .catch(error => {
          console.log(error);
        })
      } 

      markTodo = (todo) =>{
        const status = (todo.status == "Active" ? "Completed" : "Active");
        axios.patch('/todos/'+todo.id, {
            status
          })
          .then(res => {
            this.setState({
                todos: this.state.todos.map(stateTodo => {
                  if (stateTodo.id === todo.id)
                      stateTodo.status = status;
                  
                  return stateTodo;
                })
              })
          })
          .catch(error => {
            console.log(error);
          })
      }
    
      editTodo = (todo) =>{ 
        axios.patch('/todos/'+todo.id, {
          title: todo.title
        })
        .then(res => {
            this.setState({
                todos: this.state.todos.map(stateTodo => {
                  if (stateTodo.id === todo.id)
                  stateTodo.title = todo.title;
                return stateTodo;
                })
              })
        })
        .catch(error => {
          console.log(error);
        })
      }
    
      deleteTodo = (id) =>{
        axios.delete('/todos/'+id)
          .then(res => {
              this.setState({
                  todos: this.state.todos.filter(todo => {
                    return (todo.id !== id)
                  })
                })
          })
          .catch(error => {
            console.log(error);
          })
      }
    
      addTodo = (todo) => {
        axios.post('/todos/', {
          title: todo.title
        })
        .then(res => {
          let todos = [...this.state.todos, todo]
          this.setState({
            todos
          })
        })
        .catch(error => {
          console.log(error);
        })
      }
      
    render(){
        const todoList = this.state.todos.length ? (
            this.state.todos.map(todo => {
                return (
                    <Todo todo={todo} key={todo.id} markTodo={this.markTodo} editTodo={this.editTodo} deleteTodo={this.deleteTodo}/>
                )
            })
        ) : (
            <p className="center">You have no Todos</p>
        );

        return (
                <ul className="todos collection">
                    {todoList}
                    <AddTodo addTodo={this.addTodo}/>
                </ul>
        )
    }
}



export default Todos
