import React, { Component } from 'react'
import Moment from 'react-moment'
import DatePicker from "react-datepicker";
 
import "react-datepicker/dist/react-datepicker.css";
 

class Todo extends Component {
    state = {
        id: null,
        title: "",
        due_at: "",
        editing: false,
        startDate: new Date()
    }

    constructor(props){
        super(props);
        this.state = {
            id: props.todo.id,
            title: props.todo.title,
            due_at: props.todo.due_at,
            editing: false,
        }
     }
    
    toggleEdit(){
        this.setState({
            editing: true
        })
    }
    doneEdit(){
        this.setState({
            editing: false
        })
        this.props.editTodo(this.state);   
    }
    handleChange = (e) => {
        this.setState({
            title: e.target.value
        })
    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.doneEdit();
    }
    changeDate = (date) => {
        var due_date = new Date(date).toISOString().slice(0, 19).replace('T', ' ');            
        this.setState({
            due_at: due_date
        })
    }
    submitDate = (e) => {
        this.props.editTodo(this.state); 
    }
    render() {
        const {todo, markTodo, deleteTodo} = this.props;
        const calendarStrings = {
            lastDay : '[Yesterday]',
            sameDay : '[Today,] LT',
            nextDay : '[Tomorrow,] LT',
            lastWeek : '[last] dddd',
            nextWeek : 'dddd[,] LT ',
            sameElse : 'L'
        };   
        const ExampleCustomInput = ({ value, onClick }) => (
            <button className="example-custom-input datetime waves-effect waves-light white teal-text btn " onClick={onClick}><i className="material-icons left">today</i><Moment calendar={calendarStrings}>{this.state.due_at}</Moment></button> 
            //   {value}
            // </button>
          );
        return (
            
            <li className="collection-item avatar">
                <label className="circle">
                    <input type="checkbox" className="" checked={todo.status === "Completed"? "checked" : ""} onChange={() => markTodo(todo)}/>
                    <span></span>
                </label>
                {(!this.state.editing) ? <p className="title" onDoubleClick={() => this.toggleEdit()}>{this.state.title}</p> : <form onSubmit={this.handleSubmit}><input type="text" onChange={this.handleChange} onBlur={() => this.doneEdit()} value={this.state.title}/></form> }
                
                <p className="grey-text text-darken-1">{todo.description}</p>
                <a className="example-custom-input delete-btn secondary-content small material-icons red-text text-lighten-2 right" onClick={() => deleteTodo(todo.id)}>delete_forever</a>                    
                <DatePicker
                    // selected={startDate}
                    onChange={date => this.changeDate(date)}
                    customInput={<ExampleCustomInput />}
                    showTimeSelect
                    timeFormat="HH:mm"
                    timeIntervals={30}
                    timeCaption="time"
                    dateFormat="MMMM d, yyyy h:mm aa"
                    onBlur = {e => {this.submitDate()}}
                    />
            </li>
        )
               
    }
}


export default Todo;
