import React, {Component} from 'react';
import axios from 'axios'

import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class EditTodo extends Component {
    state = {
       todo: null
    };
    
    componentDidMount(){
    
        axios.defaults.baseURL = 'http://localhost:8000';
        let id = this.props.match.params.todo_id;
        axios.get('/todos/'+id)
        .then(res => {
            console.log(res.data)
            this.setState({
                todo : res.data
            })
        })

    }

    editTodo = (e) => {
        e.preventDefault();
        // this.props.addTodo(this.state);
        // this.setState({
        //     content:""
        // })

//        this.props.history.push('/todos')
    }
    changeDate = (date) => {
        var due_date = new Date(date).toISOString().slice(0, 19).replace('T', ' ');            
        this.setState({
            due_at: due_date
        })

        this.props.editTodo(this.state); 
    }
    render(){
      const startDate = new Date(this.state.todo.due_at);
        return(
            <div className="container">
                <h4 className="center">Todo Item</h4>
                <div className="row">
                    <form className="col s12" onSubmit={this.ediTodo}>
                    <div className="row">
                        <div className="input-field col s12">
                        <input id="title" type="text" className="validate" value={this.state.title}/>
                        <label htmlFor="title">Title</label>
                        </div>
                    </div>
                    <div className="row">
                        <div className="input-field col s12">
                        <input id="description" type="text" className="validate" value={this.state.description}/>
                        <label htmlFor="description">Description</label>
                        </div>
                    </div>
                   
                    <div className="row">
                        <div className="input-field col s12">
                             <DatePicker
                                selected={startDate}
                                onChange={date => this.changeDate(date)}
                                // customInput={<ExampleCustomInput />}
                                showTimeSelect
                                timeFormat="HH:mm"
                                timeIntervals={30}
                                timeCaption="time"
                                dateFormat="MMMM d, yyyy h:mm aa"
                                />
                        </div>
                    </div>
                    <button className="btn waves-effect waves-light" type="submit" name="action">Update
                        <i className="material-icons right"></i>
                    </button>
                    </form>
             </div>
             
        </div>
        
        )
    }
}

export default EditTodo
