import React, { Component } from 'react'
import Moment from 'react-moment'

class Todo extends Component {
    state = {
        id: null,
        title: "",
        status: "",
        editing: false,
    }

    constructor(props){
        super(props);
        console.log('constructor')
        this.state = {
            id: props.todo.id,
            title: props.todo.title,
            status: props.todo.status,
            editing: false,
        }
     }
    
    toggleStatus = () => {
        console.log(this.state.status);
        let toggledStatus = this.state.status === "Active" ? "Completed" : "Active";
        console.log(toggledStatus);

        this.setState({
            status: toggledStatus
        })
        console.log(this.state);
        this.props.editTodo(this.state);  
    }
    toggleEdit(){
        this.setState({
            editing: true
        })
    }
    doneEdit(){
        this.setState({
            editing: false
        })
        this.props.editTodo(this.state);   
    }
    handleChange = (e) => {
        this.setState({
            title: e.target.value
        })
    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.doneEdit();
    }
    render() {
        const {todo, markTodo, deleteTodo} = this.props;
        const calendarStrings = {
            lastDay : '[Yesterday]',
            sameDay : '[Today,] LT',
            nextDay : '[Tomorrow,] LT',
            lastWeek : '[last] dddd',
            nextWeek : 'dddd[,] LT ',
            sameElse : 'L'
        };   
        return (
            
            <li className="collection-item avatar">
                <label className="circle">
                    <input type="checkbox" className="" checked={this.state.status === "Completed"? "checked" : ""} onChange={() => this.toggleStatus()}/>
                    <span></span>
                </label>
                {(!this.state.editing) ? <p className="title" onDoubleClick={() => this.toggleEdit()}>{this.state.title}</p> : <form onSubmit={this.handleSubmit}><input type="text" onChange={this.handleChange} onBlur={() => this.doneEdit()} value={this.state.title}/></form> }
                
                <p className="grey-text text-darken-1">{todo.description}</p>
                { todo.due_at ? <a className="datetime waves-effect waves-light white teal-text btn"><i className="material-icons left">today</i><Moment calendar={calendarStrings}>{todo.due_at}</Moment></a> : ""}
                <a className="delete-btn secondary-content small material-icons red-text text-lighten-2 right" onClick={() => deleteTodo(todo.id)}>delete_forever</a>                    
                {/* <i className="edit-btn small material-icons grey-text right" onClick={() => deleteTodo(todo.id)}>edit</i>                   */}
            </li>
        )
               
    }
}


export default Todo;
