import React from 'react'
// import { Link } from 'react-router-dom'
const Todos = ({todos, markTodo, deleteTodo}) => {
    
    const todoList = todos.length ? (
        todos.map(todo => {
            return (
                
                <li className="collection-item avatar" key={todo.id}>
                    <label className="circle">
                        <input type="checkbox" className="" checked={todo.status? "checked" : ""} onChange={() => markTodo(todo.id)}/>
                        <span></span>
                    </label>
                    <span className="title">{todo.content}</span>
                    <p>I am a very simple card. I am good at containing small bits of information.</p>
                    <a className="waves-effect waves-light white teal-text btn "><i className="material-icons left">today</i>today</a>
                    <a className="secondary-content small material-icons red-text text-lighten-2 right" onClick={() => deleteTodo(todo.id)}>delete_forever</a>                    
                </li>
            )
        })
    ) : (
        <p className="center">You have no Todos</p>
    );

    return (
        <ul className="todos collection">
            {todoList}
        </ul>
    )
    
}


export default Todos;
