import React, { Component } from 'react'
import Moment from 'react-moment'
import DatePicker from "react-datepicker";
 
import "react-datepicker/dist/react-datepicker.css";
 

class Todo extends Component {
    state = {
        id: null,
        title: "",
        due_at: "",
        editing: false,
    }

    constructor(props){
        super(props);
        this.state = {
            id: props.todo.id,
            title: props.todo.title,
            due_at: props.todo.due_at,
            editing: false,
        }
     }
    
    toggleEdit(){
        this.setState({
            editing: true
        })
    }
    doneEdit(){
        this.setState({
            editing: false
        })
        this.props.editTodo(this.state);   
    }
    handleChange = (e) => {
        this.setState({
            title: e.target.value
        })
    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.doneEdit();
    }
    render() {
        const {todo, markTodo, deleteTodo} = this.props;
        const calendarStrings = {
            lastDay : '[Yesterday]',
            sameDay : '[Today,] LT',
            nextDay : '[Tomorrow,] LT',
            lastWeek : '[last] dddd',
            nextWeek : 'dddd[,] LT ',
            sameElse : 'L'
        };   
        const [startDate, setStartDate] = this.state.due_at ? this.state.due_at : new Date();
        const ExampleCustomInput = ({ value, onClick }) => (
            <button className="example-custom-input" onClick={onClick}>
              {value}
            </button>
          );
        return (
            
            <li className="collection-item avatar">
                <label className="circle">
                    <input type="checkbox" className="" checked={todo.status === "Completed"? "checked" : ""} onChange={() => markTodo(todo)}/>
                    <span></span>
                </label>
                {(!this.state.editing) ? <p className="title" onDoubleClick={() => this.toggleEdit()}>{this.state.title}</p> : <form onSubmit={this.handleSubmit}><input type="text" onChange={this.handleChange} onBlur={() => this.doneEdit()} value={this.state.title}/></form> }
                
                <p className="grey-text text-darken-1">{todo.description}</p>
                { this.state.due_at ? <a className="datetime waves-effect waves-light white teal-text btn" ><i className="material-icons left">today</i><Moment calendar={calendarStrings}>{this.state.due_at}</Moment></a> : ""}
                {/* <a className="example-custom-input delete-btn secondary-content small material-icons red-text text-lighten-2 right" onClick={() => deleteTodo(todo.id)}>delete_forever</a>                     */}
                <DatePicker
                    selected={startDate}
                    onChange={date => setStartDate(date)}
                    customInput={ExampleCustomInput}
                    showTimeSelect
                    timeFormat="HH:mm"
                    timeIntervals={30}
                    timeCaption="time"
                    dateFormat="MMMM d, yyyy h:mm aa"
                    />
                {/* <i className="edit-btn small material-icons grey-text right" onClick={() => deleteTodo(todo.id)}>edit</i>                   */}
            </li>
        )
               
    }
}


export default Todo;
