import React, {Component} from 'react';
import axios from 'axios'
import moment from 'moment'

import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class EditTodo extends Component {
    state = {
       title: "",
       description: "",
       due_at: "",
    };
    
    componentDidMount(){
    
        axios.defaults.baseURL = 'http://localhost:8000';
        let id = this.props.match.params.todo_id;
        axios.get('/todos/'+id)
        .then(res => {
            console.log(res.data)
            this.setState({
                title: res.data.title,
                description: res.data.description,
                due_at: res.data.due_at,
            })
        })
        .catch(error => {
            console.log(error);
          })
    }

    editTodo = (e) => {
        e.preventDefault();
        axios.patch('/todos/'+id)
        .then(res => {
            console.log(res.data)
            this.setState({
                title: res.data.title,
                description: res.data.description,
                due_at: res.data.due_at,
            })
        })
        .catch(error => {
            console.log(error);
          })
        // this.props.addTodo(this.state);
        // this.setState({
        //     content:""
        // })

//        this.props.history.push('/todos')
    }
    changeDate = (date) => {
        var due_date = moment(date).format('YYYY-MM-DD HH:mm:ss');            
        this.setState({
            due_at: due_date
        })

        //this.props.editTodo(this.state); 
    }
    render(){
    
        const todo = this.state.title ? (
            <div className="container">
                <h4 className="center">Todo Item</h4>
                <div className="row">
                    <form className="col s12" onSubmit={this.ediTodo}>
                    <div className="row">
                        <div className="input-field col s12">
                        <input id="title" type="text" className="validate" onChange={this.changeTitle} value={this.state.title}/>
                        <label htmlFor="title">Title</label>
                        </div>
                    </div>
                    <div className="row">
                        <div className="input-field col s12">
                        <input id="description" type="text" className="validate" onChange={this.changeDescription} value={this.description}/>
                        <label htmlFor="description">Description</label>
                        </div>
                    </div>
                   
                    <div className="row">
                        <div className="input-field col s12">
                             <DatePicker
                                selected={new Date(this.state.due_at)}
                                onChange={date => this.changeDate(date)}
                                // customInput={<ExampleCustomInput />}
                                showTimeSelect
                                timeFormat="HH:mm"
                                timeIntervals={30}
                                timeCaption="time"
                                dateFormat="MMMM d, yyyy h:mm aa"
                                />
                        </div>
                    </div>
                    <button className="btn waves-effect waves-light" type="submit" name="action">Update
                        <i className="material-icons right"></i>
                    </button>
                    </form>
             </div>
             
        </div>
        
        )
        :
        (<div className="center">Loading post...</div>)

        return (
            <div className="container">
               {todo}
            </div>
        )
    }
}

export default EditTodo
