            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        token: state.token
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        destroyToken: () => { dispatch(destroyToken())}
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Logout)
