import React, { Component } from 'react'
import Moment from 'react-moment'

class Todos extends Component {
    toggleEdit(e){
      console.log(e);
        //todo.editing = true;
    }
    render() {
       const {todos, markTodo, deleteTodo} = this.props;
        const calendarStrings = {
            lastDay : '[Yesterday]',
            sameDay : '[Today,] LT',
            nextDay : '[Tomorrow,] LT',
            lastWeek : '[last] dddd',
            nextWeek : 'dddd[,] LT ',
            sameElse : 'L'
        };
    
        const todoList = todos.length ? (
            todos.map(todo => {
                return (
                    
                    <Todo todo={todo} />
                )
            })
        ) : (
            <p className="center">You have no Todos</p>
        );


        return (
                <ul className="todos collection">
                    {todoList}
                </ul>
        )
    }
}



export default Todos;
