import React, { Component } from 'react'
import Moment from 'react-moment'

class Todo extends Component {
    state = {
        editing: false,
        title: ""
    }

    constructor(props){
        super(props);
        this.state = {
            editing: false,
            title: props.todo.title
        }
     }
    
    toggleEdit(){
        this.setState({
            editing: true
        })
    }
    doneEdit(e){
        e.preventDefault();
        this.setState({
            editing: false
        })
    }
    handleChange = (e) => {
        this.setState({
            title: e.target.value
        })
    }
    handleSubmit = (e) => {
      
        this.doneEdit();
        this.props.editTodo(this.state);
    }
    render() {
        const {todo, markTodo, deleteTodo} = this.props;
        const calendarStrings = {
            lastDay : '[Yesterday]',
            sameDay : '[Today,] LT',
            nextDay : '[Tomorrow,] LT',
            lastWeek : '[last] dddd',
            nextWeek : 'dddd[,] LT ',
            sameElse : 'L'
        };   
        return (
            
            <li className="collection-item avatar">
                <label className="circle">
                    <input type="checkbox" className="" checked={todo.status === "Completed"? "checked" : ""} onChange={() => markTodo(todo.id)}/>
                    <span></span>
                </label>
                {(!this.state.editing) ? <p className="title" onDoubleClick={() => this.toggleEdit()}>{this.state.title}</p> : <form onSubmit={() => this.doneEdit()}><input type="text" onChange={this.handleChange} onBlur={() => this.doneEdit()} value={this.state.title}/></form> }
                
                <p className="grey-text text-darken-1">{todo.description}</p>
                { todo.due_at ? <a className="datetime waves-effect waves-light white teal-text btn"><i className="material-icons left">today</i><Moment calendar={calendarStrings}>{todo.due_at}</Moment></a> : ""}
                <a className="delete-btn secondary-content small material-icons red-text text-lighten-2 right" onClick={() => deleteTodo(todo.id)}>delete_forever</a>                    
            </li>
        )
               
    }
}


export default Todo;
