import React, { Component } from 'react';
import Todos from './components/Todos'
import AddTodo from './components/AddTodo'
import Navbar from './components/Navbar'
import { BrowserRouter, Route, Switch} from 'react-router-dom'
import Home from './components/Home'
import About from './components/About'
import Login from './components/auth/Login'
import Logout from './components/auth/Logout'
import Post from './components/Post'
import axios from 'axios'

class App extends Component {
  state = {
    token: null,
    todos: [
      // {id: 1, title: "Buy Milk", description: "I am a very simple card. I am good at containing small bits of information.",  due_at:"2020-07-26 19:30:00", status: true},
      // {id: 2, title: "Buy Something", due_at:"2020-07-24 19:30:00",  status: false},
      // {id: 3, title: "Date Next week", due_at:"2020-07-29 19:30:00",  status: false},
      // {id: 4, title: "Date Next week", due_at:"2020-08-05 19:30:00",  status: true},
      // {id: 5, title: "Last Week Date ", due_at:"2020-06-19 19:30:00",  status: false}
    ]
  };


  componentDidMount(){
    axios.defaults.baseURL = 'http://localhost:8000';
    axios.get('/todos')
    .then(res => {
        console.log(res);
        this.setState({
            todos: res.data.slice(0,10)
        })
    })
} 


  loggedIn = () => {
    return this.state.token !== null
  }
  markTodo = (id) =>{
    this.setState({
      todos: this.state.todos.map(todo => {
        if (todo.id === id)
          todo.status = !todo.status;

      return todo;
      })
    })
  }

deleteTodo = (id) =>{
    this.setState({
      todos: this.state.todos.filter(todo => {
        return (todo.id !== id)
      })
    })

  }

  addTodo = (todo) => {
    todo.id=this.state.todos.length+1
    todo.status=false
    let todos = [...this.state.todos, todo]
    this.setState({
      todos
    })
  }
  render(){
    return (
      <BrowserRouter>
        <div className="App container">
          <Navbar loggedIn={this.loggedIn}/>
          <Switch>
            <Route exact path='/' component={Home} />
            <Route path='/todos' render={() => <div><Todos todos={this.state.todos}  markTodo={this.markTodo} deleteTodo={this.deleteTodo}/> <AddTodo addTodo={this.addTodo}/></div> } />
            <Route path='/about' component={About} />
            <Route path='/login' component={Login} />
            <Route path='/logout' component={Logout} />
            <Route path='/:post_id' component={Post}/>
          </Switch>

        </div>
      </BrowserRouter>
    );
  }
}

export default App;
