import React from 'react'
import { Link } from 'react-router-dom'
const Todos = ({todos, markTodo, deleteTodo}) => {
    
    const todoList = todos.length ? (
        todos.map(todo => {
            return (
                
<div className="collection-item" key={todo.id}>
                 <label>
                    <input type="checkbox" className="filled-in" checked={todo.status? "checked" : ""} onChange={() => markTodo(todo.id)}/>
                    <span> <div className="card">
                    <div className="card-content">
                    <a className="small material-icons red-text text-lighten-2 right" onClick={() => deleteTodo(todo.id)}>delete_forever</a>
                    <span className="card-title">{todo.content}</span>
                    <p>I am a very simple card. I am good at containing small bits of information.
                    I am convenient because I require little markup to use effectively.</p>
                    <a class="waves-effect waves-light white teal-text btn"><i class="material-icons left">today</i>button</a>
                    </div>
                   
                </div></span>
                </label>
                <span><Link to="/todo"></Link></span>
                    
                    
                </div>

               
                


                
            )
        })
    ) : (
        <p className="center">You have no Todos</p>
    );

    return (
        <div className="todos collection">
            {todoList}
        </div>
    )
    
}


export default Todos;
