import axios from 'axios';

const initState = {
    token: null,
    posts: [
            {id: 1, title: "Buy Milk 1", body: "I am a very simple card. I am good at containing small bits of information."},
            {id: 2, title: "Buy Milk 2", body: "I am a very simple card. I am good at containing small bits of information."},
            {id: 3, title: "Buy Milk 3", body: "I am a very simple card. I am good at containing small bits of information."},
            {id: 4, title: "Buy Milk 4", body: "I am a very simple card. I am good at containing small bits of information."},
    ]
}
const rootReducer = (state = initState, action) => {
    if(action.type === 'GET_TOKEN') {
        
        axios.post('/oauth/token/', {
            grant_type: 'password',
            client_id: 2,
            client_secret: 'NFAjDhC2pruCS3d9W2Ewfggavr5lsKFLbSyrSJrs',
            username: action.username,
            password: action.password
          })
          .then(res => {
          console.log(res);
          })
          .catch(error => {
            console.log(error);
          })

        return  {
            ...state,
            token: null
        }
    }
    else if(action.type === 'DELETE_POST') {
        let newPosts = state.posts.filter(post => post.id !== action.id);

        return {
            ...state,
            posts: newPosts
        }
    }    

    return state;
}

export default rootReducer
