import React, {Component} from 'react'

class EditTodo extends Component {
    state = this.props.location.state.todo
 
    editTodo = (e) => {
        e.preventDefault();
        // this.props.addTodo(this.state);
        // this.setState({
        //     content:""
        // })

//        this.props.history.push('/todos')
    }
    render(){
         // const ExampleCustomInput = ({ value, onClick }) => (
        //     <button className="example-custom-input datetime waves-effect waves-light white teal-text btn " onClick={onClick}><i className="material-icons left">today</i><Moment calendar={calendarStrings}>{value ? value : this.state.due_at}</Moment></button> 
        //     //   {value}
        //     // </button>
        //   );
        return(
            <div className="container">
                <h4 className="center">Todo Item</h4>
                <div className="row">
                    <form className="col s12" onSubmit={this.ediTodo}>
                    <div className="row">
                        <div className="input-field col s12">
                        <input id="title" type="text" className="validate" value={this.state.title}/>
                        <label htmlFor="title">Title</label>
                        </div>
                    </div>
                    <div className="row">
                        <div className="input-field col s12">
                        <input id="first_name" type="text" className="validate"/>
                        <label htmlFor="first_name">First Name</label>
                        </div>
                    </div>
                   
                    <div className="row">
                        <div className="input-field col s12">
                            <input id="email" type="email" className="validate" />
                            <label htmlFor="email">Email</label>
                        </div>
                    </div>
                    <button className="btn waves-effect waves-light" type="submit" name="action">Update
                        <i className="material-icons right"></i>
                    </button>
                    </form>
             </div>
              {/* <DatePicker
                    // selected={startDate}
                    onChange={date => this.changeDate(date)}
                    customInput={<ExampleCustomInput />}
                    showTimeSelect
                    timeFormat="HH:mm"
                    timeIntervals={30}
                    timeCaption="time"
                    dateFormat="MMMM d, yyyy h:mm aa"
                    onBlur = {e => {this.submitDate()}}
                    /> */}
        </div>
        
        )
    }
}

export default EditTodo
