import React, { Component } from 'react'
import Moment from 'react-moment'

class Todos extends Component {
    toggleEdit(todo){
      window.alert('Double CLicked');
        todo.editing = true;
    }
    render() {
       const {todos, markTodo, deleteTodo} = this.props;
        const calendarStrings = {
            lastDay : '[Yesterday]',
            sameDay : '[Today,] LT',
            nextDay : '[Tomorrow,] LT',
            lastWeek : '[last] dddd',
            nextWeek : 'dddd[,] LT ',
            sameElse : 'L'
        };
    
        const todoList = todos.length ? (
            todos.map(todo => {
                return (
                    
                    <li className="collection-item avatar" key={todo.id}>
                        <label className="circle">
                            <input type="checkbox" className="" checked={todo.status == "Completed"? "checked" : ""} onChange={() => markTodo(todo.id)}/>
                            <span></span>
                        </label>
                        {(!todo.editing) ? <p className="title" onDoubleClick={()=>this.toggleEdit(todo)}>{todo.title}</p> : <input type="text"  value={todo.title}/>}
                        
                        <p className="grey-text text-darken-1">{todo.description}</p>
                       { todo.due_at ? <a className="datetime waves-effect waves-light white teal-text btn"><i className="material-icons left">today</i><Moment calendar={calendarStrings}>{todo.due_at}</Moment></a> : ""}
                        <a className="delete-btn secondary-content small material-icons red-text text-lighten-2 right" onClick={() => deleteTodo(todo.id)}>delete_forever</a>                    
                    </li>
                )
            })
        ) : (
            <p className="center">You have no Todos</p>
        );


        return (
                <ul className="todos collection">
                    {todoList}
                </ul>
        )
    }
}



// const Todos = () => {
  
    


export default Todos;
